import React, {useState} from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

export default function NewExpense(props) {
  const [InitialState, setInitialState] = useState(true)
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  };


  if (props.expenses.length === 0) {
    return <h3 className="expenses-list__fallback">No expenses found.</h3>;
  } else {
    return (
      <ul className="expenses-list">
        {props.expenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            date={expense.date}
            title={expense.title}
            amount={expense.amount}
          />
        ))}
      </ul>
    );
  }
  if (InitialState === true) {
    return (
    <div className="new-expense"><button>Add new expense</button></div>
    )} else {
  return (
    <div className="new-expense">
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} setInitialState={setInitialState}/>
    </div>
  )}}
